import React, { Component } from 'react';
import Selected from './Pokemon';
import { handleGetEachPokemon,
         showMyTeam
} from '../api/api'

export default class MyTeam extends Component {
    constructor(props){
        super(props)
        this.state = {
            pokemon: {},
            myTeam: {},
            selected: false
        }
    }

    componentDidMount(){

    }
    handleGetMyTeam = () =>{
        console.log(this.props.email)
        showMyTeam(this.props.email);
    }

    renderMyTeam=() =>{
        // return this.props.pokemons.map((pokemon)=>{
        //     let id = pokemon.name
        //    return(
        //     < OriginalPokemons 
        //         key={id}
        //         id ={id}
        //         name ={pokemon.name}
        //         setSelectedPokemon = {this.setSelectedPokemon}
        //     />
        //    )
        // })
    }
    setSelectedPokemon =(pokemon)=>{
        handleGetEachPokemon(pokemon)
            .then(selected =>{
                let {data} = selected
                this.setState({
                    pokemon: data,
                    selected: true
                })

            })

            .catch(error =>{
                console.log(error)
            })
    }

    render() {
        return (
            <>
            <button onClick={this.handleGetMyTeam}>click me!</button>
            {
                this.state.selected ? (
                    <Selected pokemon ={this.state.pokemon}
                              email ={this.props.email}
                    />
                ):(
                    <div className="row">
                    {/* {this.showOriginalPokemons()} */}
                    </div>
                ) 
            }
            </>
        )
    }
}
