import React, { Component } from 'react';
import { handleGetAllPokemons } from './api/api';

class App extends Component {
  constructor(props){
    super(props)
    this.state ={
      pokemons: [],
      isAuth: false
    }
  }

  componentDidMount(){
    this.appHandleGetAllPokemons()
  }

  appHandleGetAllPokemons = ()=>{
    handleGetAllPokemons()
      .then(pokemon => {
        const { data } = pokemon;
        this.setState({
          pokemons: data.results
        })
      })
      .catch(error =>{
        console.log(error)
      })
  }

  render() {
    return (
      <div>
        {this.state.pokemons.map(pokemon =>{

          <h1>{pokemon.name}</h1>
        }
        )}
      </div>
    )
  }
}

export default App;
