import React, { Component } from 'react';
import { apiAuth } from '../api/api';

export default class Login extends Component {

    constructor(props) {
        super(props);

        this.state = {
            email: '',
            password: '',
            errorToggle: false,
            errorMessage: '',
        }


    } 

    handleInputOnChange = (event) => {
        console.log(event.target.value)
        this.setState({
            [event.target.name]: event.target.value
        })

    }

    handleInputSubmit = (event) => {
        event.preventDefault(); 
        apiAuth({
            email: this.state.email,
            password: this.state.password
        })
        .then(result =>{
            console.log(result)
            const { email } = result
            this.setState({
                isAuth: true,
                loggedInEmail: email
            })
        })
        .catch(error =>{
            let errorMessages = JSON.stringify(error.response.data.message) 
            this.setState({
                errorToggle: true,
                errorMessage: errorMessages
            })
        })
    }
    render() {
        return (
          <div class="wrapper fadeInDown">
          <div id="formContent">

            <div class="fadeIn first">
              <img src="http://danielzawadzki.com/codepen/01/icon.svg" id="icon" alt="User Icon" />
            </div>

            <form>
              <input type="text" id="login" class="fadeIn second" name="login" placeholder="login"></input>
              <input type="text" id="password" class="fadeIn third" name="login" placeholder="password"></input>
              <input type="submit" class="fadeIn fourth" value="Log In"></input>
            </form>

            <div id="formFooter">
              <a class="underlineHover" href="#">Forgot Password?</a>
            </div>

          </div>
        </div>
        )
    }
}
