import React, { Component } from 'react';
import { handleGetAllPokemons } from './api/api';
import uuid from 'uuid/v4';

class App extends Component {
  constructor(props){
    super(props)
    this.state ={
      pokemons: [{
        id: '',
        name: '',
        url: ''
      }],
      isAuth: false
    }
  }

  componentDidMount(){
    this.appHandleGetAllPokemons()
  }

  appHandleGetAllPokemons = ()=>{
    handleGetAllPokemons()
      .then(pokemon => {
        const { data } = pokemon;
        this.setState({
          pokemons: [{
            id: uuid(),
            name: data.result.name
          }]
        })
      })
      .catch(error =>{
        console.log(error)
      })
  }

  render() {
    return (
      <div>
      </div>
    )
  }
}

export default App;
