import { Axios } from './Axios';
import axios from 'axios';
import jwt_decode from 'jwt-decode';

const handleGetAllPokemons =()=>{
    return new Promise((resolve, reject)=>{
        axios.get('http://pokeapi.co/api/v2/pokemon/?limit=151')
            .then(pokemon =>{
                resolve(pokemon)
            })
            .catch((error)=>{
                reject(error)
            })
    })
}

const apiAuth = ({email, password}) =>{
    return new Promise((resolve, reject)=>{
        Axios.post('/users/sign-up-and-sign-in', {
            email,
            password
        })
        .then(results => {
            let { token }= results.data
            localStorage.setItem('jwtToken', token)
            const decodedToken = jwt_decode(token)
            resolve(decodedToken)
            resolve(results)
        })
        .catch(error =>{
            reject(error)
        })
    })
}
const checkToken = () =>{
    Axios.get('/users/auth')
}
const logOut = () =>{
    Axios.get('/users/logout')
        .then(cookie => {
            document.cookie = 'name=access_token; expires=Thu, 01 Jan 1970 00:00:00 UTC;'
        })
}

export {
    handleGetAllPokemons,
    apiAuth,
    checkToken,
    logOut
}
