import React, { Component } from 'react';
import Nav from './components/Nav';
import Home from './components/Home';
import OriginalPokemons from './components/OriginalPokemons';
import { Switch, Route } from 'react-router-dom';
import jwt_decode from 'jwt-decode';
import { handleGetAllPokemons,
         checkToken
} from './api/api';


class App extends Component {
  constructor(props){
    super(props)
    this.state ={
      pokemons: [],
      isAuth: false,
      email: ''
    }
  }

  componentDidMount(){

    this.appHandleLocalStorage();
  }

  appHandleLocalStorage = () =>{
    checkToken()
      .then(token =>{
        this.setState({
          isAuth: true,
          email: token.email
        })
      })
      .catch(error =>{
        this.setState({
          isAuth: false
        })
      })

  }

  appHandleHome=(email)=>{
  return(
    this.setState({
      isAuth: true,
      email: email
    })
    )
  }

  appHandleLogOut= () =>{
    return (
      this.setState({
        isAuth: false
      })
    )
  }

  appHandleGetAllPokemons = ()=>{
    handleGetAllPokemons()
      .then(pokemon => {
        const { data } = pokemon;
        this.setState({
          pokemons: data.results
        })
      })
      .catch(error =>{
        console.log(error)
      })
  }



  render() {
    return(
      <>
      <Nav appHandleHome={this.appHandleHome}
            isAuth = {this.state.isAuth}
            email ={this.state.email}
            appHandleLogOut = {this.appHandleLogOut}
      />
      <Switch>
      {/* { 
        this.state.isAuth ? (
          <Home/>
      ):(
      <h1>Must be logged in to learn more</h1>
      )
      } */}

        <Route path='/original151' render={()=><OriginalPokemons
            pokemons ={this.state.pokemons}/>}/>
      </Switch>
      </>
    )
  }
}

export default App;
