import React, { Component } from 'react';


export default class OriginalPokemons extends Component {
    constructor(props){
        super(props)
        this.state = {
            pokemon: this.props.name,
            selected: false
        }
    }
    handleMoreInfo = () =>{
        this.props.setSelectedPokemon(this.state.pokemon)
    }

    render() {
        const{
            id,
            name
        } = this.props
        return (
            <div className="col-sm d-flex" key ={id}>
                <div className="card" >
                {/* <img className="card-img-top" src="..." alt="Card image cap"></img> */}
                    <div className="card-body">
                        <h5 className="card-title">{name}</h5>
                        <button className="btn  btn-sm" onClick={this.handleMoreInfo}>More Info</button>
                    </div>
                </div>
            </div>
        )
    }
}
