import React, { Component } from 'react';
import Nav from './components/Nav';
import Home from './components/Home';
import jwt_decode from 'jwt-decode';
import { handleGetAllPokemons,
         checkToken
} from './api/api';

class App extends Component {
  constructor(props){
    super(props)
    this.state ={
      pokemons: [],
      isAuth: false,
      email: ''
    }
  }

  componentDidMount(){
    this.appHandleGetAllPokemons();
    this.appHandleLocalStorage();
  }

  appHandleLocalStorage = () =>{
    checkToken()
      .then(token =>{
        this.setState({
          isAuth: true,
          email: token.email
        })
      })
      .catch(error =>{
        this.setState({
          isAuth: false
        })
      })

  }

  appHandleHome=(email)=>{
  return(
    this.setState({
      isAuth: true,
      email: email
    })
    )
  }

  appHandleGetAllPokemons = ()=>{
    handleGetAllPokemons()
      .then(pokemon => {
        const { data } = pokemon;
        this.setState({
          pokemons: data.results
        })
      })
      .catch(error =>{
        console.log(error)
      })
  }

  render() {
    return(
      <>
      {this.state.email}
      <Nav appHandleHome={this.appHandleHome}/>
      {this.state.isAuth? (<Home/>):(<h1>Must be logged in</h1>)}

      </>
    )
  }
}

export default App;
