import React, { Component } from 'react';
import { addPokemon } from '../api/api';
import {Redirect} from 'react-router-dom';


export default class Pokemon extends Component {
    handleAddPokemon = () =>{
      const { pokemon, email } = this.props
      addPokemon( email, pokemon.name, pokemon.sprites.front_default)
        .then(result =>{
          return (
            <Redirect to='/my-team'/>
          )
        })
        .catch(error =>{
          console.log(error)
        })
    }

    render() {
      const { pokemon } = this.props
        return (
                        <div className="col">
                        <div className="card" >
                        <img className="rounded mx-auto d-block" src={pokemon.sprites.front_default} alt="Card image cap"></img>
                        <div className="card-body">
                          <h5 className="card-title">{pokemon.name}</h5>
                          <a className="btn btn-primary" onClick={this.handleAddPokemon}>I choose you!</a>
                          <table className="table table-striped">
                      <thead>
                        <tr>
                          <th scope="col">Moves</th>
                        </tr>
                      </thead>
                      <tbody>
                          {
                        pokemon.abilities.map(ability =>{
                          {/* <th scope="row">Abilities</th> */}
                          return(
                            <tr>

                            <td>{ability.ability.name}</td>
                        </tr>
                          )

                        })
                      }

                      </tbody>
                      <thead>
                        <tr>
                          <th scope="col">Moves</th>
                        </tr>
                      </thead>
                      <tbody>
                          {
                        pokemon.moves.map(move =>{
                          {/* <th scope="row">Abilities</th> */}
                          return(
                            <tr>

                            <td>{move.move.name}</td>
                        </tr>
                          )

                        })
                      }

                      </tbody>

                    </table>
                        </div>
                      </div>
                    </div>
        )
    }
}
