import React, { Component } from 'react';
import { apiAuth } from '../api/api';

export default class Nav extends Component {

    constructor(props) {
        super(props);

        this.state = {
            email: '',
            password: '',
            errorToggle: false,
            errorMessage: '',
        }


    } 

    handleInputOnChange = (event) => {
        console.log(event.target.value)
        this.setState({
            [event.target.name]: event.target.value
        })

    }

    handleInputSubmit = (event) => {
        event.preventDefault(); 
        apiAuth({
            email: this.state.email,
            password: this.state.password
        })
        .then(result =>{
            console.log(result)
            const { email } = result
            this.setState({
                isAuth: true,
                loggedInEmail: email
            })
        })
        .catch(error =>{
            let errorMessages = JSON.stringify(error.response.data.message) 
            this.setState({
                errorToggle: true,
                errorMessage: errorMessages
            })
        })
    }
    render() {
        return (
				<nav className="navbar navbar-expand-lg navbar-light bg-light rounded mt-5">
				  <a className="navbar-brand" href="#">Pokemon</a>
				  <button className="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
				    <span className="navbar-toggler-icon"></span>
				  </button>
				  <div className="collapse navbar-collapse" id="navbarSupportedContent">
				    <ul className="navbar-nav mr-auto">
				      <li className="nav-item active">
				        <a className="nav-link" href="#">Home <span className="sr-only">(current)</span></a>
				      </li>
				    </ul>
                    { 
                this.state.isAuth ? ( 
                    <>
                    <h5 className="nav-item">{this.state.loggedInEmail}</h5>
                <button className="btn btn-danger">Log-out</button>
                    </>
                ) : (
				    <form className="form-inline my-2 my-lg-0" onSubmit={this.handleInputSubmit}>
				    	<div className="col-auto pl-0">
					      <div className="input-group">
					        <div className="input-group-prepend">
					          <div className="input-group-text"><i className="fa fa-user"></i></div>
					        </div>
					        <input type="text" className="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" placeholder="Email Address" name="email" onChange={this.handleInputOnChange}></input>
					      </div>
					    </div>
					    <div className="col-auto pl-0">
					      <div className="input-group">
					        <div className="input-group-prepend">
					          <div className="input-group-text"><i className="fa fa-lock"></i></div>
					        </div>
					        <input type="text" className="form-control" id="inlineFormInputGroup" placeholder="Password" name="password" onChange={this.handleInputOnChange}></input>
					      </div>
					    </div>
					    <div className="col-auto pl-0 pr-0">
				      		<button className="btn btn-primary my-2 my-sm-0" type="submit">Login</button>
				      	</div>
				    </form>
                )
                    }
				  </div>
				</nav>	
        )
    }
}
