import React, { Component } from 'react';
import Selected from './Pokemon';
import MyTeamPokemons from './MyTeamPokemons'
import { handleGetEachPokemon,
         showMyTeam
} from '../api/api'

export default class MyTeam extends Component {
    constructor(props){
        super(props)
        this.state = {
            pokemon: {},
            myTeam: [],
            selected: false
        }
    }

    componentDidMount(){
        this.handleGetMyTeam(this.props.email)
    }
    handleGetMyTeam = (email) =>{
        showMyTeam(email)
            .then(result =>{
                let { data } = result
                console.log(data)
                this.setState({
                    myTeam: data.pokemons
                })
            })
            .catch(error =>{
                console.log(error)
            })
    }

    renderMyTeam=() =>{
        return this.props.myTeam.map((pokemon)=>{
           return(
            < MyTeamPokemons 
                key={pokemon.id}
                id ={pokemon.id}
                name ={pokemon.name}
                setSelectedPokemon = {this.setSelectedPokemon}
            />
           )
        })
    }
    setSelectedPokemon =(pokemon)=>{
        handleGetEachPokemon(pokemon)
            .then(selected =>{
                let {data} = selected
                this.setState({
                    pokemon: data,
                    selected: true
                })

            })

            .catch(error =>{
                console.log(error)
            })
    }

    render() {
        return (
            <>
            <button onClick={this.handleGetMyTeam}>click me!</button>
            {console.log(this.state.myTeam)}
            {
                this.state.selected ? (
                    <Selected pokemon ={this.state.pokemon}
                              email ={this.props.email}
                    />
                ):(
                    <div className="row">
                    {this.renderMyTeam()}
                    </div>
                ) 
            }
            </>
        )
    }
}
