import React, { Component } from 'react';
import Nav from './components/Nav';
import Home from './components/Home';
import jwt_decode from 'jwt-decode';
import { handleGetAllPokemons,
         checkToken
} from './api/api';

class App extends Component {
  constructor(props){
    super(props)
    this.state ={
      pokemons: [],
      isAuth: false,
      email: ''
    }
  }

  componentDidMount(){
    this.appHandleGetAllPokemons();
    this.appHandleLocalStorage();
  }

  appHandleLocalStorage = () =>{
    checkToken()
      .then(token =>{
        this.setState({
          isAuth: true,
          email: token.email
        })
      })
      .catch(error =>{
        this.setState({
          isAuth: false
        })
      })

  }

  appHandleHome=(email)=>{
  return(
    this.setState({
      isAuth: true,
      email: email
    })
    )
  }

  appHandleLogOut= () =>{
    return (
      this.setState({
        isAuth: false
      })
    )
  }

  appHandleGetAllPokemons = ()=>{
    handleGetAllPokemons()
      .then(pokemon => {
        this.setState({
          pokemons: pokemon
        })
      })
      .catch(error =>{
        console.log(error)
      })
  }

  render() {
    return(
      <>
      <Nav appHandleHome={this.appHandleHome}
            isAuth = {this.state.isAuth}
            email ={this.state.email}
            appHandleLogOut = {this.appHandleLogOut}
      />
      { 
        this.state.isAuth ? (
      <Home name={this.state.email}
            pokemons ={this.state.pokemons}
      />
      ):(
      <h1>Must be logged in</h1>
      )
      }

      </>
    )
  }
}

export default App;
