import React, { Component } from 'react';
import OriginalPokemons from './OriginalPokemons';
import Selected from './Pokemon';
import { handleGetEachPokemon } from '../api/api'

export default class Pokemons extends Component {
    constructor(props){
        super(props)
        this.state = {
            pokemon: {},
            selected: false
        }
    }
    showOriginalPokemons =() =>{
        return this.props.pokemons.map((pokemon)=>{
            let id = pokemon.name
           return(
            < OriginalPokemons 
                key={id}
                id ={id}
                name ={pokemon.name}
                setSelectedPokemon = {this.setSelectedPokemon}
            />
           )
        })
    }
    setSelectedPokemon =(pokemon)=>{
        handleGetEachPokemon(pokemon)
            .then(selected =>{
                let {data} = selected
                this.setState({
                    pokemon: data,
                    selected: true
                })

            })

            .catch(error =>{
                console.log(error)
            })
    }

    render() {
        return (
            <>
            <form className="form-inline my-2 my-lg-0" >
            <input class="form-control" type="text" placeholder="Search" aria-label="Search"></input>
            <button className="btn  btn-sm btn-outline-light" type="submit">Search</button>
            </form>
            {
                this.state.selected ? (
                    <Selected pokemon ={this.state.pokemon}
                              email ={this.props.email}
                    />
                ):(
                    <div className="row">
                    {this.showOriginalPokemons()}
                    </div>
                ) 
            }
            </>
        )
    }
}
