import React, { Component } from 'react';
import Login from './components/Nav';
import Home from './components/Home';
import jwt_decode from 'jwt-decode';
import { BrowserRouter, Switch, Route, Redirect } from 'react-router-dom';
import { handleGetAllPokemons,
         checkToken
} from './api/api';

class App extends Component {
  constructor(props){
    super(props)
    this.state ={
      pokemons: [],
      isAuth: false,
      email: ''
    }
  }

  componentDidMount(){
    this.appHandleGetAllPokemons();
    this.appHandleLocalStorage();
  }

  appHandleLocalStorage = () =>{
    checkToken()
      .then(token =>{
        this.setState({
          isAuth: true,
        })
      })
      .catch(error =>{
        return (
          <Redirect push to="/login"/>
        )
      })

  }

  appHandleGetAllPokemons = ()=>{
    handleGetAllPokemons()
      .then(pokemon => {
        const { data } = pokemon;
        this.setState({
          pokemons: data.results
        })
      })
      .catch(error =>{
        console.log(error)
      })
  }

  render() {
    return(
    <BrowserRouter>
        <Switch>
    {this.state.isAuth? (<Route exact path="/" render={()=>(
      <Home appHandleLocalStorage= {this.appHandleLocalStorage} />
    )}/>) :
    ( <Redirect to="/login"/>)}
      <Route  exact path="/login" component={Login}/>
        </Switch>
        </BrowserRouter>
    )
  }
}

export default App;
