import React, { Component } from 'react';
import { handleGetAllPokemons } from './api/api';
import uuid from 'uuid/v4';

class App extends Component {
  constructor(props){
    super(props)
    this.state ={
      pokemons: [{
        id: '',
        name: '',
        url: ''
      }],
      isAuth: false
    }
  }

  componentDidMount(){
    this.appHandleGetAllPokemons()
  }

  appHandleGetAllPokemons = ()=>{
    handleGetAllPokemons()
      .then(pokemon => {
        console.log(pokemon)
        const { data } = pokemon;
        data.results.map(results => {
          return    this.setState({
            pokemons: [{
              id: uuid(),
              name: results.name,
              url: results.url
            }]
          })
        })

      })
      .catch(error =>{
        console.log(error)
      })
  }

  render() {
    return (
      <div>
        {console.log(this.state.pokemons)}
      </div>
    )
  }
}

export default App;
